import React, { useRef, useEffect, useState  } from "react";
import PropTypes from "prop-types";
import "./VideoVid.scss"

import { apiKey } from "../../pages/Main/Main";

function VideoVid({ selectedVideo, isPlaying, videoRef }) {
    const videoUrlWithApiKey = selectedVideo
        ? `${selectedVideo.video}?api_key=${apiKey}`
        : "";
        const [videoKey, setVideoKey] = useState(0);
        useEffect(() => {
            // increment the key when selectedVideo changes to force remount
            setVideoKey(prevKey => prevKey + 1);
        }, [selectedVideo]);
        useEffect(() => {
            const video = videoRef.current;

            const playVideo = () => {
                if (video && video.paused) {
                    video.play();
                }
            };

            document.addEventListener("click", playVideo);

            return () => {
                document.removeEventListener("click", playVideo);
            };
        }, [videoKey]);

    useEffect(() => {
        const video = videoRef.current;

        if (video && isPlaying) {
            const playPromise = video.play();

            if (playPromise !== undefined) {
                playPromise.then(() => {
                })
                    .catch(error => {
                        console.error("Auto-play was prevented:", error);
                    });
            }
        } else if (video) {
            video.pause();
        }
    }, [isPlaying]);

    return (
        <video 
        ref={videoRef} 
        key={videoKey}
        className='video-vid' 
        poster={selectedVideo ? selectedVideo.image : ""}>
            <source className='video-vid__source' src={videoUrlWithApiKey} />
        </video>
    );
}

VideoVid.propTypes = {
    selectedVideo: PropTypes.object,
    isPlaying: PropTypes.bool,
};

export default VideoVid;
