// VideoControls.js
import React, { useState, useEffect } from "react";
import Play from "../../assets/images/icons/play.svg";
import Pause from "../../assets/images/icons/pause.svg";
import Expand from "../../assets/images/icons/fullscreen.svg";
import Volume from "../../assets/images/icons/volume_off.svg";

import "./VideoControls.scss";

function VideoControls({ selectedVideo, duration }) {
    const [isPlaying, setIsPlaying] = useState(false);
    const [currentTime, setCurrentTime] = useState(0);

    const formatTime = (timeInSeconds) => {
        if (typeof timeInSeconds !== 'number' || isNaN(timeInSeconds)) {
            return '0:00';
        }
        const minutes = Math.floor(timeInSeconds / 60);
        const seconds = Math.floor(timeInSeconds % 60);
        return `${minutes}:${seconds < 10 ? '0' : ''}${seconds}`;
    };

    // handle play/pause button click
    const handlePlayPause = () => {
        const video = videoRef.current;
        if (isPlaying) {
            video.pause();
        } else {
            video.play();
        }
        setIsPlaying(!isPlaying);
    };

    // handle time update during video playback
    const handleTimeUpdate = () => {
        const video = videoRef.current;
        setCurrentTime(video.currentTime);
    };

    // attach event listeners when the component mounts
    useEffect(() => {
        const video = videoRef.current;

        // function to handle when the video can start playing
        const handleCanPlay = () => {
            if (isPlaying) {
                video.play();
            }
        };

        video.addEventListener('canplay', handleCanPlay);
        video.addEventListener('timeupdate', handleTimeUpdate);

        return () => {
            video.removeEventListener('canplay', handleCanPlay);
            video.removeEventListener('timeupdate', handleTimeUpdate);
        };
    }, [isPlaying]);

    return (
        <div className='video-controls'>
            <button className='video-controls__play' onClick={handlePlayPause}>
                <img
                    className='video-controls__play-icon'
                    src={isPlaying ? Pause : Play}
                    alt={isPlaying ? "Pause" : "Play"}
                />
            </button>
            <div className='video-controls__eq'>
                <div className='video-controls__scrub'>
                    <input
                        type="range"
                        className="video-controls__scrubber"
                        value={currentTime}
                        max={duration}
                    />
                    <p className='video-controls__scrub-time'>{`${formatTime(currentTime)}/${formatTime(duration)}`}</p>
                </div>
            </div>
            <div className='video-controls__right'>
                <p className='video-controls__right-link'>
                    <img className='video-controls__right-expand' src={Expand} alt="Full Screen" />
                </p>
                <p className='video-controls__right-link'>
                    <img className='video-controls__right-volume' src={Volume} alt="Volume On/Off" />
                </p>
            </div>
        </div>
    );
}

export default VideoControls;
